export default {
  data: function data() {
    return {
      levelList: null
    };
  },
  watch: {
    $route: function $route() {
      this.getBreadcrumb();
    }
  },
  methods: {
    getBreadcrumb: function getBreadcrumb() {
      //$route.matched一个数组 包含当前路由的所有嵌套路径片段的路由记录
      var matched = this.$route.matched.filter(function (item) {
        return item.name;
      });
      this.levelList = matched;
    }
  },
  created: function created() {
    this.getBreadcrumb();
  }
};
